package Week_5_Workshop;

public class question_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Employee e = new Employee("4600 kathmandu");
		e.department = "IT"; 
		e.display();
	}

}

class Person{
	protected String address;
	
	public Person(String address) {
		this.address = address;
	}
}

class Employee extends Person{
	public String department;
	
	public Employee(String address) {
		super(address);
	}
	
	public void display() {
		System.out.println("The address of Employee is "+ address);
		System.out.println("The department_address of Employee is "+ department);
	}
}

